import React, {useState, useContext, useEffect} from 'react'
import ContactContext from '../../context/contact/contactContext'

const ContactForm = () => {

    const contactContext = useContext(ContactContext)

    const {addContact, current} = contactContext

    const {name, email, phone, type} = contact;

    useEffect(() => {
        if(current!==null){
            setContact(current)
        } else{
            setContact({
                name:'',
                email:'',
                phone:'',
                type:'personal'
            });
        }
    }, [contactContext, current])

    const [contact, setContact] = useState({
        name:'',
        email:'',
        phone:'',
        type:'personal'
    })



    const onChange = e => setContact({ ...contact, [e.target.name]: e.target.value });

    const onSubmit = e => {
        e.prevetDefault()
        addContact(contact)
        setContact({
            name:'',
            email:'',
            phone:'',
            type:'personal'
        })
    }

    return (
        <form onSubmit={onSubmit}>
            <h2 className='text-primary'>Add Contact</h2>
            <input 
                type="text" 
                name="name" 
                placeholder="Name" 
                value={name}
                onChange={onChange}>
            </input>
            <input 
                type="email" 
                name="email" 
                placeholder="Email" 
                value={email}
                onChange={onChange}>
            </input>
            <input
                type='text'
                placeholder='Phone'
                name='phone'
                value={phone}
                onChange={onChange}
            />
            <h4>Contact Type</h4>
            <input
                type='radio'
                name='type'
                value='personal'
                checked={type === 'personal'}
                onChange={onChange}
            />{' '}
            Personal{' '}
            <input
                type='radio'
                name='type'
                value='professional'
                checked={type === 'professional'}
                onChange={onChange}
            />{' '}
             Professional  
            <div>
                <input type="submit" value="Add Contact" className="btn btn-primary btn-block"></input>
            </div>
        </form>

    )
}

export default ContactForm
